import { BaseWidgetProps, DiagramEngine, BaseWidget, DefaultPortLabel } from "storm-react-diagrams";

import * as React from "react";
import * as _ from "lodash";
import { Node } from "../models/Node";
import { Port } from "../models/Port";
import { PortLabel } from "./PortLabelWidget";

export interface NodeProps extends BaseWidgetProps {
	node: Node;
	diagramEngine: DiagramEngine;
}

export interface DefaultNodeState {}

/**
 * @author Dylan Vorster
 */
export class NodeWidget extends BaseWidget<NodeProps, DefaultNodeState> {
	constructor(props: NodeProps) {
		super("srd-default-node", props);
		this.state = {};
	}

	generatePort(port: Port) {
		return <PortLabel model={port} key={port.id} />;
	}

	render() {
		return (
			<div {...this.getProps()} style={{ background: this.props.node.color }}>
				<div className={this.bem("__title")}>
					<div className={this.bem("__name")}>{this.props.node.name}</div>
				</div>
				<div className={this.bem("__ports")}>
					<div className={this.bem("__in")}>
						{_.map(this.props.node.getPorts(), this.generatePort.bind(this))}
					</div>
					{/* <div className={this.bem("__in")}>
						{_.map(this.props.node.getOutPorts(), this.generatePort.bind(this))}
					</div> */}
				</div>
			</div>
		);
	}
}
