import * as SRD from "storm-react-diagrams";
import { NodeFactory } from "../factories/NodeFactory";
import { PortLabelFactory } from "../factories/PortLabelFactory";
import { Node } from "../models/Node";

/**
 * @author Dylan Vorster
 */
export class Application {
	protected activeModel: SRD.DiagramModel;
	protected diagramEngine: SRD.DiagramEngine;

	constructor() {
		this.diagramEngine = new SRD.DiagramEngine();
		this.diagramEngine.installDefaultFactories(); 

		this.diagramEngine.registerNodeFactory(new NodeFactory());
		this.diagramEngine.registerPortFactory(new PortLabelFactory());

		this.newModel();
	}

	public newModel() {
		this.activeModel = new SRD.DiagramModel();
		this.diagramEngine.setDiagramModel(this.activeModel);
		console.log('MODEL JSON', JSON.stringify(this.activeModel.serializeDiagram(), null, 2))
		//3-A) create a default node
		var node1 = new Node("Node 1", "rgb(0,192,255)");
		let port = node1.addOutPort("Out");
		node1.setPosition(100, 100);

		//3-B) create another default node
		var node2 = new Node("Node 2", "rgb(192,255,0)");
		let port2 = node2.addInPort("In");
		node2.setPosition(400, 100);

		// link the ports
		let link1 = port.link(port2);

		this.activeModel.addAll(node1, node2, link1);
	}

	public getActiveDiagram(): SRD.DiagramModel {
		return this.activeModel;
	}

	public getDiagramEngine(): SRD.DiagramEngine {
		return this.diagramEngine;
	}
}
