import React, { useState } from 'react'
import Popup from 'reactjs-popup'
import sqlFormatter from "sql-formatter";
import './popup.scss';
import axios, { AxiosResponse } from 'axios';

class Props {
  isOpen: boolean;
  serializeDiagram: any;
  fU: () => void;
}

export const PopupExample = (props: Props) => {

  const [dbName, setDbName] = useState(
    'DB1'
  );

  const [sqlString, setSqlString] = useState(
    ''
  );

  const aa = sqlString && sqlFormatter.format(sqlString, {
    language: "sql", // Defaults to "sql"
    indent: "    "   // Defaults to two spaces
  });
  console.log('AAAA', props, aa);


  return (

    <Popup
      modal
      closeOnDocumentClick
      open={props.isOpen}
      closeOnEscape
    >
      <div className="SQLResultDialog">
      <input type="text" onChange={event => setDbName(event.target.value)} value={dbName}/>
      <button onClick={ () => {
        setSqlString('');
        props.fU();

        let x = JSON.stringify(props.serializeDiagram, null, 2);
        let res = axios.post('http://localhost:5000/api/setjob', { SerializedDiagram: x, DatabaseName: dbName })
          .then((response) => {
            setSqlString(response.data); 
            props.fU();
          })
          .catch(function (error) {
            console.log(error);
          });
      }}>Generate</button>
        <textarea readOnly={true}>{aa}</textarea>
        {/* <button>Download</button> */}
      </div>
    </Popup>)
}
