import React, { useState } from 'react'
import Popup from 'reactjs-popup'
import sqlFormatter from "sql-formatter";
import './popup.scss';
import { Link } from './components/models/Link';
import { Label } from './components/models/Label';
import { Node } from './components/models/Node';

import { NodeModel } from 'storm-react-diagrams';
import { RelationType } from './App2';

class Props {
  isOpen: boolean;
  link: Link;
  update: () => void;
}

export const RelationPopup = (props: Props) => {

  const [left, setLeft] = useState(
    '1, N'
  );

  const [right, setRight] = useState(
    '0, 1'
  );

  const [relationName, setRelationName] = useState(
    'relation'
  );

  const [relType, setRelType] = useState(
    0
  );

  let update = () => {
    let labels = props.link.labels as Label[];
    labels[0].label = left;
    labels[4].label = right;
    labels[2].label = relationName;
    let points = props.link.points;
    for (let index = 1; index < points.length - 1; index++) {

      const element = points[index];
      element.remove();
    }
    props.link.relationType = relType;
    props.update()
  }
console.log('WWWW', props);

const sourcePort = props.link && props.link.sourcePort.parent as Node;
const targetPort = props.link && props.link.targetPort.parent as Node;
  return (


    <Popup
      modal
      closeOnDocumentClick
      open={props.isOpen}
      closeOnEscape
    >
      <div className="SQLResultDialog">
        <p>{sourcePort && sourcePort.name}</p>
        <select className="darkSelect"
          onChange={(event) => setLeft(event.target.value)}
          value={left}>
          <option value="1, N">1, N</option>
          <option value="0, N">0, N</option>
          <option value="0, 0">0, 0</option>
          <option value="0, 1">0, 1</option>
          <option value="1, 1">1, 1</option>
          <option value="1, 0">1, 0</option>
          <option value="N, M">N, M</option>

        </select>

        <input className="darkInput" type="text" defaultValue={relationName} onChange={(event) => setRelationName(event.target.value)}></input>

        <p>{targetPort && targetPort.name}</p>

        <select className="darkSelect"
          onChange={(event) => setRight(event.target.value)}
          value={right}>
          <option value="1, N">1, N</option>
          <option value="0, N">0, N</option>
          <option value="0, 0">0, 0</option>
          <option value="0, 1">0, 1</option>
          <option value="1, 1">1, 1</option>
          <option value="1, 0">1, 0</option>
          <option value="N, M">N, M</option>

        </select>

        <select className="darkSelect"
          onChange={(event) => setRelType(event.target.value as unknown as number)}
          value={relType}>
          <option value={0}>020</option>
          <option value={1}>02M</option>
          <option value={2}>M2M Attr</option>
          <option value={3}>M2M no Attr</option>

        </select>
        <button onClick={() => update()}>SAVE</button>
        {/* <input type="text" onChange={event => setDbName(event.target.value)} value={dbName}/>
      <button onClick={ () => props.generateScript(dbName)}>Generate</button>
        <textarea readOnly={true} value={aa}></textarea>
        <button>Download</button> */}
      </div>
    </Popup>)
}
