import * as React from "react";
import * as _ from "lodash";
import { DefaultNodeModel, DiagramWidget, BaseEntityType, BaseEntity, BaseModelListener, BaseModel, DiagramModel, DefaultLinkModel } from "storm-react-diagrams";
import { TrayItemWidget } from "./components/dragAndDrop/TrayItemWidget";
import { TrayWidget } from "./components/dragAndDrop/TrayWidget";
import { Application } from "./components/dragAndDrop/Application";
import './dragAndDrop.scss';
require("storm-react-diagrams/dist/style.min.css");

import './App.css';
import { NodeInfo } from "./components/nodeInfo/NodeInfo";
import { Node } from "./components/models/Node";
import { Link } from "./components/models/Link";
import { PropertyType } from "./components/models/PropertyType";

export interface BodyWidgetProps {
	app: Application;
}

export interface BodyWidgetState {
	// selectedNode: BaseModel<BaseEntity, BaseModelListener>[]; //DefaultNodeModel
	selectedNode: Node;
	diagramModel: DiagramModel;
	selectedLink: Link;
	selectedRelation: RelationType;
}

export enum RelationType {
	O2O = 0,
    O2M = 1,
    M2M = 2
}

export class App2 extends React.Component<BodyWidgetProps, BodyWidgetState> {
	constructor(props: BodyWidgetProps) {
		super(props);
		this.state = {
			selectedNode: null,
			diagramModel: null,
			selectedLink: null,
			selectedRelation: RelationType.O2O
		}
	}

	componentWillMount(){
		this.setState({selectedNode: null})
	}

	render() {
		return (
			<div className="body">
				<div className="header">
					<div className="title">Create diagrams</div>
				</div>
				<div className="content">
					<TrayWidget>
						<TrayItemWidget model={{ type: "table" }} name="Node" color="rgb(0,192,255)" />
						<TrayItemWidget model={{ type: "label" }} name="Label" color="rgb(192,255,0)" />

						<div
							style={{ borderColor: "rgb(0,255,0)" , marginTop: '100px'
						}}
							className={`tray-item ${this.state.selectedRelation === RelationType.O2O ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getSelectedItems()[0] as Node;
								app.updatePorts(RelationType.O2O);
								this.setState({
									selectedRelation: RelationType.O2O
								})
							}}
						>
							One2One Relation
						</div>
						<div
							style={{ borderColor: "rgb(0,255,0)"}}
							className={`tray-item ${this.state.selectedRelation === RelationType.O2M ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getSelectedItems()[0] as Node;
								app.updatePorts(RelationType.O2M);
								this.setState({
									selectedRelation: RelationType.O2M
								})							}}
						>
							One2Many Relation
						</div>
						<div
							style={{ borderColor: "rgb(0,255,0)"}}
							className={`tray-item ${this.state.selectedRelation === RelationType.M2M ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getSelectedItems()[0] as Node;
								app.updatePorts(RelationType.M2M);
								this.setState({
									selectedRelation: RelationType.M2M
								})							}}
						>
							Many2Many Relation
						</div>
						<div
							style={{ borderColor: "rgb(255,0,0)", marginTop: '100px'}}
							className="tray-item"
							onClick={() => {
								console.log(JSON.stringify(this.props.app.getActiveDiagram().serializeDiagram(), null, 2));
							}}
						>
							Serialize Graph
						</div>


					</TrayWidget>
					<div
						className="diagram-layer"
						onDrop={event => {
							var data = JSON.parse(event.dataTransfer.getData("storm-diagram-node"));
							var nodesCount = _.keys(
								this.props.app
									.getDiagramEngine()
									.getDiagramModel()
									.getNodes()
							).length;

							var node = null;
							if (data.type === "table") {
								node = new Node("table","Node " + (nodesCount + 1), "rgb(0,192,255)");
								node.addInPort(true, false, "Id",this.state.selectedRelation, this.props.app.getActiveDiagram(), PropertyType.INT);
								node.addInPort(false,false,"Name",this.state.selectedRelation, this.props.app.getActiveDiagram(), PropertyType.NVARCHAR);
							} else {
								node = new Node("label","Label ", "rgb(192,255,0)");
							}
							console.log('event', node);

							var points = this.props.app.getDiagramEngine().getRelativeMousePoint(event);
							node.x = points.x;
							node.y = points.y;
							this.props.app
								.getDiagramEngine()
								.getDiagramModel()
								.addNode(node);
							this.forceUpdate();
						}}
						onDragOver={event => {
							event.preventDefault();
						}}

						onDoubleClick={event => {
							event.preventDefault();

							this.setState({
								selectedNode: this.props.app
								.getDiagramEngine()
								.getDiagramModel()
								.getSelectedItems()[0] as Node,
								diagramModel: this.props.app.getDiagramEngine().getDiagramModel(),
								selectedLink: this.props.app
								.getDiagramEngine()
								.getDiagramModel()
								.getSelectedItems()[1] as Link,
							});													
						}}
					>
						<DiagramWidget className="srd-demo-canvas" diagramEngine={this.props.app.getDiagramEngine()} />

					</div>
					{ this.state.selectedNode != null ? <NodeInfo selectedRelation={this.state.selectedRelation} selectedLink={this.state.selectedLink} selectedItem={this.state.selectedNode} diagramModel={this.state.diagramModel} /> : null}
				</div>
			</div>
		);
	}
}
