import * as React from "react";
import * as _ from "lodash";
import { DefaultNodeModel, DiagramWidget, BaseEntityType, BaseEntity, BaseModelListener, BaseModel, DiagramModel, DefaultLinkModel, DiagramEngine } from "storm-react-diagrams";
import { TrayItemWidget } from "./components/dragAndDrop/TrayItemWidget";
import { TrayWidget } from "./components/dragAndDrop/TrayWidget";
import { Application } from "./components/dragAndDrop/Application";
import './dragAndDrop.scss';
require("storm-react-diagrams/dist/style.min.css");
require('react-bootstrap-table-next/dist/react-bootstrap-table2.min.css');
import SQLResultDialog from "./components/SQLResultDialog/SQLResultDialog";
import './App.css';
import { NodeInfo } from "./components/nodeInfo/NodeInfo";
import { Node } from "./components/models/Node";
import { Link } from "./components/models/Link";
import { PropertyType } from "./components/models/PropertyType";
import axios, { AxiosResponse } from 'axios';
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import { NodeProperties } from "./components/nodeProperties/NodeProperties";
import Popup from 'reactjs-popup'
import { PopupExample } from "./PopupExample";
import { GenerationHandler } from "./GenerationHandler";
import { RelationPopup } from "./RelationPopup";
import AppContext from "./context/appContext/AppContext";

export interface BodyWidgetProps {
	app: Application;
}

export interface BodyWidgetState {
	showDialog: boolean;
	sqlString: AxiosResponse;
	selectedNode: Node;
	diagramEngine: DiagramEngine;
	selectedLink: Link;
	selectedRelation: RelationType;
	isLocked: boolean;
	showRelationDialog: boolean;
	activeView: AppViewType
}

export enum AppViewType {
	ENTITY = 0,
	RELATION = 1
}

export enum RelationType {
	O2O = 0,
	O2M = 1,
	M2M = 2,
	none = 3,
	M2MNoTable = 4
}

export class App2 extends React.Component<BodyWidgetProps, BodyWidgetState> {

	static contextType = AppContext;

	constructor(props: BodyWidgetProps) {
		super(props);
		this.state = {
			sqlString: null,
			showDialog: false,
			selectedNode: null,
			diagramEngine: null,
			selectedLink: null,
			selectedRelation: RelationType.O2O,
			isLocked: false,
			showRelationDialog: false,
			activeView: AppViewType.ENTITY
		}
	}

	componentWillMount() {
		this.setState({ selectedNode: null })
	}

	updateRelationPopup() {
		this.setState({ showRelationDialog: false,  selectedNode: null});
		// this.forceUpdate();
	}


	render() {
		function handleClick(e: any, data: any) {
			console.log(data.foo);
		}
		return (
			<div className="body">
				<div className="header">
					<div className="title">DB Diagram Scripter</div>
				</div>
				<div className="content">
					<TrayWidget>
						<TrayItemWidget model={{ type: "table" }} name="Node" color="rgb(0,192,255)" />
						<TrayItemWidget model={{ type: "label" }} name="Label" color="rgb(192,255,0)" />


						<div
							style={{
								borderColor: "rgb(0,255,0)", marginTop: '100px'
							}}
							className={`tray-item ${this.state.selectedRelation === RelationType.O2O ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getNodes() as { [s: string]: Node };
								Object.keys(app).forEach(x => app[x].updatePorts(RelationType.O2O));
								this.setState({
									selectedRelation: RelationType.O2O
								})

							}}
						>
							Relation{/* One2One Relation */}
						</div>
						{/* <div
							style={{ borderColor: "rgb(0,255,0)" }}
							className={`tray-item ${this.state.selectedRelation === RelationType.O2M ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getNodes() as { [s: string]: Node };
								Object.keys(app).forEach(x => app[x].updatePorts(RelationType.O2M));

								this.setState({
									selectedRelation: RelationType.O2M
								})

							}}
						>
							One2Many Relation
						</div>
						<div
							style={{ borderColor: "rgb(0,255,0)" }}
							className={`tray-item ${this.state.selectedRelation === RelationType.M2M ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getNodes() as { [s: string]: Node };
								Object.keys(app).forEach(x => app[x].updatePorts(RelationType.M2M));
								this.setState({
									selectedRelation: RelationType.M2M
								})
							}}
						>
							Many2Many Atributes Relation
						</div>

						<div
							style={{ borderColor: "rgb(0,255,0)" }}
							className={`tray-item ${this.state.selectedRelation === RelationType.M2MNoTable ? 'selected' : ''}`}
							onClick={() => {
								let app = this.props.app.getActiveDiagram().getNodes() as { [s: string]: Node };
								Object.keys(app).forEach(x => app[x].updatePorts(RelationType.M2MNoTable));
								this.setState({
									selectedRelation: RelationType.M2MNoTable // RelationType.M2MNoTable
								})
							}}
						>
							Many2Many No Atributes Relation
						</div> */}






						{/* <div
							style={{ borderColor: "rgb(255,0,0)", marginTop: '100px'}}
							className="tray-item"
							onClick={() => {
								console.log(JSON.stringify(this.props.app.getActiveDiagram().serializeDiagram(), null, 2));
							}}
						>
							Serialize Graph
						</div> */}

						<div
							style={{ borderColor: "rgb(255,0,0)", marginTop: '100px' }}
							className={`tray-item ${this.context.view === AppViewType.ENTITY ? 'selected' : ''}`}
							onClick={() => {

								this.context.changeViewType(0);
							}}
						>
							Entity View

						</div>

						<div
							style={{ borderColor: "rgb(255,0,0)" }}
							className={`tray-item ${this.context.view === AppViewType.RELATION ? 'selected' : ''}`}
							onClick={() => {

								// this.setState({ activeView: AppViewType.RELATION });
								this.context.changeViewType(1);

							}}
						>
							Relation View

						</div>

						<div
							style={{ borderColor: "rgb(255,0,0)" }}
							className="tray-item"
							onClick={() => {

								this.setState({ showDialog: true });
							}}
						>
							Create DB Code

						</div>



					</TrayWidget>

					<div
						className="diagram-layer"
						onDrop={event => {
							var data = JSON.parse(event.dataTransfer.getData("storm-diagram-node"));
							var nodesCount = _.keys(
								this.props.app
									.getDiagramEngine()
									.getDiagramModel()
									.getNodes()
							).length;

							var node = null;
							if (data.type === "table") {
								node = new Node("table", `Node${nodesCount + 1}`, "rgb(0,192,255)");
								node.addInPort(true, true, true, true, false, "Id", this.state.selectedRelation, this.props.app.getDiagramEngine(), PropertyType.INT);
								node.addInPort(false, false, false, false, false, "Name", this.state.selectedRelation, this.props.app.getDiagramEngine(), PropertyType.NVARCHAR);
							} else {
								node = new Node("label", "Label ", "rgb(192,255,0)");
							}

							var points = this.props.app.getDiagramEngine().getRelativeMousePoint(event);
							node.x = points.x;
							node.y = points.y;
							this.props.app
								.getDiagramEngine()
								.getDiagramModel()
								.addNode(node);
							this.forceUpdate();
						}}
						onDragOver={event => {
							event.preventDefault();
						}}

						onClick={event => {
							event.preventDefault();
							this.setState({
								selectedRelation: RelationType.none,
								showDialog: false
							})
						}}

// 						onMouseOverCapture={event => {
// 							event.preventDefault();

// 							if (this.props.app
// 								.getDiagramEngine()
// 								.getDiagramModel().getSelectedItems()[0] !== undefined) {
// 									console.log('0000', this.props.app
// 									.getDiagramEngine()
// 									.getDiagramModel().getSelectedItems()[0].parent as Link);

// 								let a = (this.props.app
// 								.getDiagramEngine()
// 								.getDiagramModel().getSelectedItems()[0].parent as Link).sourcePort;
// 								let b = (this.props.app
// 									.getDiagramEngine()
// 									.getDiagramModel().getSelectedItems()[0].parent as Link).targetPort;
// a.setSelected(true);
// b.setSelected(true);

// 							}
// 						}}



						onDoubleClick={event => {
							event.preventDefault();

							console.log('OOOO', this.props.app.getDiagramEngine().getDiagramModel().getSelectedItems());
if (this.props.app.getDiagramEngine().getDiagramModel().getSelectedItems().length === 1) {
	this.setState({
		selectedLink: this.props.app
			.getDiagramEngine()
			.getDiagramModel().getSelectedItems()[0].parent as Link,
		showRelationDialog: true,
		selectedNode: null
	})
} else {
this.setState({
								showDialog: false,
								showRelationDialog: false,
								selectedNode: this.props.app
									.getDiagramEngine()
									.getDiagramModel()
									.getSelectedItems()[0] as Node,
								diagramEngine: this.props.app.getDiagramEngine()
								// ,
								// selectedLink: this.props.app
								// 	.getDiagramEngine()
								// 	.getDiagramModel()
								// 	.getSelectedItems()[1] as Link,
							});
}

						}}
					>
						<DiagramWidget deleteKeys={[46]} className="srd-demo-canvas" diagramEngine={this.props.app.getDiagramEngine()} />

					</div>
					{this.state.selectedNode != null ? <NodeProperties selectedRelation={this.state.selectedRelation} selectedLink={this.state.selectedLink} selectedItem={this.state.selectedNode} diagramEngine={this.state.diagramEngine} /> : null}
					{/* {this.state.showDialog ? <SQLResultDialog sqlStringResponse={this.state.sqlString} /> : null} */}
					<GenerationHandler isOpen={this.state.showDialog} serializeDiagram={this.props.app.getActiveDiagram().serializeDiagram()} />
					<RelationPopup diagramModel={this.state.diagramEngine.diagramModel} update={() => this.updateRelationPopup()} isOpen={this.state.showRelationDialog} link={this.state.selectedLink} />
				</div>
			</div >
		);

	}
}
