import { NodeModel, DefaultPortModel, Toolkit, DiagramEngine, DiagramModel } from "storm-react-diagrams";
import * as _ from "lodash";
import { Port } from "./Port";
import { PropertyType } from "./PropertyType";
import { RelationType } from "../../App2";

export class Node extends NodeModel{

    name: string;
	color: string;
	ports: { [s: string]: Port };
	selectedRelation: RelationType;

	constructor(type: string, name: string = "Untitled", color: string = "rgb(0,192,255)") {
		super(type);
		this.name = name;
		this.color = color;
		this.ports = {};
	}

	addInPort(isPrimaryKey: boolean, isForeignKey: boolean, label: string, selectedRelation: RelationType, diagramEngine: DiagramEngine, propertyType?: PropertyType ): Port {
		this.selectedRelation= selectedRelation;
		return this.addPort(new Port(isPrimaryKey,isForeignKey,true, Toolkit.UID(), label, selectedRelation, diagramEngine,  propertyType));
	}

	addOutPort(isPrimaryKey: boolean, isForeignKey: boolean, label: string, selectedRelation: RelationType, diagramEngine: DiagramEngine, propertyType?: PropertyType ): Port {
		this.selectedRelation= selectedRelation;		
		return this.addPort(new Port(isPrimaryKey,isForeignKey,true, Toolkit.UID(), label, selectedRelation, diagramEngine,  propertyType));
	}

	updatePorts(selectedRelation: RelationType) {
		let ports = this.getInPorts();
		ports.forEach(p => p.updatePortRelation(selectedRelation));
	}

	deSerialize(object: any, engine: DiagramEngine) {
		super.deSerialize(object, engine);
		this.name = object.name;
		this.color = object.color;
	}

	serialize() {
		return _.merge(super.serialize(), {
			name: this.name,
			color: this.color
		});
	}

	getInPorts(): Port[] {
		return _.filter(this.ports, portModel => {
			return portModel.in;
		});
	}

	getOutPorts(): Port[] {
		return _.filter(this.ports, portModel => {
			return !portModel.in;
		});
	}
}
