import React, { useState } from 'react'
import Popup from 'reactjs-popup'
import sqlFormatter from "sql-formatter";
import './popup.scss';
import axios, { AxiosResponse } from 'axios';

class Props {
  isOpen: boolean;
  generateScript: (name: string) => void;
  sqlString: string;
}

export const PopupExample = (props: Props) => {

  const [dbName, setDbName] = useState(
    'DB1'
  );

  const [sqlString, setSqlString] = useState(
    ''
  );

  const aa = sqlString && sqlFormatter.format(sqlString, {
    language: "sql", // Defaults to "sql"
    indent: "    "   // Defaults to two spaces
  });
  console.log('AAAA', props, aa);


  return (

    <Popup
      modal
      closeOnDocumentClick
      open={props.isOpen}
      closeOnEscape
    >
      <div className="SQLResultDialog">
      <input type="text" onChange={event => setDbName(event.target.value)} value={dbName}/>
      <button onClick={ () => props.generateScript(dbName)}>Generate</button>
        <textarea readOnly={true}>{props.sqlString}</textarea>
        {/* <button>Download</button> */}
      </div>
    </Popup>)
}
