import * as React from "react";
import { BaseWidget, BaseWidgetProps, PortWidget } from "storm-react-diagrams";
import { Port } from "../models/Port";

export interface PortLabelProps extends BaseWidgetProps {
	model: Port;
}

export interface DefaultPortLabelState {}

/**
 * @author Dylan Vorster
 */
export class PortLabel extends BaseWidget<PortLabelProps, DefaultPortLabelState> {
	constructor(props: PortLabelProps) {
		super("srd-default-port", props);
	}

	getClassName() {
		return super.getClassName() + (this.props.model.in ? this.bem("--in") : this.bem("--out"));
	}

	render() {
        var port = <PortWidget node={this.props.model.getParent()} name={this.props.model.name} />;

		var label = <div className="name">{this.props.model.label +'X'+ this.props.model.propertyType}</div>;

		return (
			<div {...this.getProps()}>
                <div>
				    {this.props.model.in ? port : label}
                </div>
                <div>
		    		{this.props.model.in ? label : port}
                </div>
			</div>
		);
	}
}
