import './NodeProperty.scss'
import * as React from "react";
import { BaseModel, BaseEntity, BaseModelListener, DefaultNodeModel } from 'storm-react-diagrams';
import { Col, Grid, Row } from "../../grid";

class OwnProps {
    value: string;
    acceptChangeProperty: (newValue: string) => void
}

class State {
    propertyValue: string;
    copy: string;
    showInput: boolean;
}

type Props = OwnProps;

export class NodeProperty extends React.Component<Props, State> {
    constructor(props: Props) {
        super(props);
    }

    componentWillMount() {
        this.setState({
            propertyValue: this.props.value,
            copy: this.props.value,
            showInput: false
        })
    }

    componentWillReceiveProps(nextProps: Props){
        if(nextProps != this.props) {
            this.setState({
                propertyValue: nextProps.value,
                copy: nextProps.value
            })
            this.forceUpdate();
        }
    }

    handleChange(event: React.ChangeEvent<HTMLInputElement>) {
        event.persist()

        this.setState({copy: event.target.value});
    }

    switchViewUpdateValue(): void {
        this.props.acceptChangeProperty(this.state.copy);
        this.setState({showInput: false});
    }

    showInput(): void {
        this.setState({
            showInput: true
        })
    }

    render() {
        return (
            <div onDoubleClick={() => this.showInput()} className="trayBottom">
            <Grid>
                <Row>
                    {this.state.showInput ? <input className="tray-bottom-item" type="text" onBlur={this.switchViewUpdateValue.bind(this)} onChange={this.handleChange.bind(this)} value={this.state.copy}></input> : <h1 className="tray-bottom-item">{this.props.value}</h1>}
                </Row>
            </Grid>
            </div>
        );
    }



}
