import './nodeItems.scss';
import * as React from "react";
import { DefaultNodeModel, DefaultPortModel, PortModel, DiagramModel, LabelModel, DefaultLinkModel, DiagramEngine } from 'storm-react-diagrams';
import { NodeProperty } from './nodeProperty/NodeProperty';
import { number } from 'prop-types';
import { randomBytes } from 'crypto';
import { Link } from '../models/Link';
import { Node} from "../models/Node";
import { PropertyType } from '../models/PropertyType';
import { Port } from '../models/Port';
import { RelationType } from '../../App2';

class OwnProps {
    selectedItem: Node;
    diagramEngine: DiagramEngine;
    selectedLink: Link;
    selectedRelation: RelationType;
}

class State {
    updatedItem: Node
    name: string;
    showInput: boolean;
    showInputDelete: boolean;
    showInputUpdate: boolean;
    showInputColor: boolean;
    portState: string;
    newPortType: PropertyType;
    newPortName: string;
    newPortIsPK: boolean;
    newPortIsFK: boolean;
    portToRemove: Port;
    portLabelToRemove: string;
    color: string
}

type Props = OwnProps;

export class NodeInfo extends React.Component<Props, State> {
    constructor(props: Props) {
        super(props);
    }

    componentWillMount() {
        this.setState({
            updatedItem: this.props.selectedItem,
            name: this.props.selectedItem.name,
            showInput: false,
            showInputDelete: false,
            showInputUpdate: false,
            showInputColor: false,
            portState: 'out',
            newPortName: 'out',
            newPortIsPK: false,
            newPortIsFK: false,
            newPortType: PropertyType.INT,
            portToRemove: null,
            portLabelToRemove: null,
            color: 'Red'
        })
    }

    componentWillReceiveProps(nextProps: Props) {


        if (nextProps != this.props) {
            this.setState({
                updatedItem: nextProps.selectedItem,
                name: nextProps.selectedItem.name
            })
            this.forceUpdate();
        }
        console.log('STATE', this.state);
    }

    changeName1(newValue: string): void {
        let updatedItem: Node = this.state.updatedItem;
        updatedItem.name = newValue;
        this.setState({ updatedItem: updatedItem });
    }

    addPort(): void {
        if (this.state.portState == 'out') {
            this.state.updatedItem.addOutPort(this.state.newPortIsPK, this.state.newPortIsFK,this.state.newPortName, this.props.selectedRelation, this.props.diagramEngine, this.state.newPortType);
        } else {
            this.state.updatedItem.addInPort(this.state.newPortIsPK, this.state.newPortIsFK,this.state.newPortName, this.props.selectedRelation, this.props.diagramEngine, this.state.newPortType);
        }
    }

    switchViewUpdateValue(): void {
        this.addPort();
        this.setState({ showInput: false });
    }

    cancelAddingPort(): void {
        this.setState({ showInput: false, showInputDelete: false, showInputColor: false });
    }

    cancelUpdatingPort(): void {
        this.setState({ showInput: false, showInputDelete: false, showInputColor: false });
    }

    cancelChangingColor(): void {
        this.setState({ showInputColor: false });
    }
    cancelInputDelete(): void {
        this.setState({ showInputDelete: false });
    }

    handleChange(event: React.ChangeEvent<HTMLInputElement>) {
        event.persist()

        this.setState({ newPortName: event.target.value });
    }

    handleChangePK(event: React.ChangeEvent<HTMLInputElement>) {
        event.persist()

        this.setState({ newPortIsPK: !this.state.newPortIsPK });
    }

    handleChangeFK(event: React.ChangeEvent<HTMLInputElement>) {
        event.persist()

        this.setState({ newPortIsFK: !this.state.newPortIsFK});
    }

    handleChangePortType(event: React.ChangeEvent<HTMLInputElement>) {
        event.persist()

        this.setState({ newPortType: PropertyType.INT });
    }



    removePort() {

        if (this.state.portToRemove != null) {
            if(this.state.portToRemove.isForeignKey) {
                let portToDelete: Port = this.state.updatedItem.getPortFromID(this.state.portToRemove.id) as Port;
                let portLinks = portToDelete.getLinks() as { [s: string]: Link };
                if(portLinks) {
                    let xd = Object.keys(portLinks);
                    this.props.diagramEngine.getDiagramModel().removeLink(xd[0]);
                }
                this.state.updatedItem.removePort(portToDelete);
                this.props.diagramEngine.recalculatePortsVisually();
                this.setState({ portToRemove: null })
            } else if(this.state.portToRemove) {
                let portToDelete: Port = this.state.updatedItem.getPortFromID(this.state.portToRemove.id) as Port;

                this.state.updatedItem.removePort(portToDelete);
                this.props.diagramEngine.recalculatePortsVisually();
                this.setState({ portToRemove: null })
            } 
        }

    }

    render() {
        let allPorts: { [S: string]: Port } = this.state.updatedItem.ports;
        console.log('XDD', allPorts);

        this.state.updatedItem
        if (allPorts != null) {
            let aaa = Object.values(allPorts).map(value => {
                return value.label;
            });

            let portsIds = Object.keys(allPorts).map((key) => {
                return key
            });

            let makeItem = function (x: any) {
                return <option key={x}>{x}</option>;
            }

            if (this.state.updatedItem === null) return 'Loading...';
            return (
                <div className="trayBottom">

                    <NodeProperty value={this.state.name} acceptChangeProperty={this.changeName1.bind(this)} />
                    {this.props.selectedItem.type !== 'label' ?
                    <div>
                    <div onDoubleClick={() => this.showInput()}>
                        {this.state.showInput ?
                            <div>
                                <input className="tray-bottom-item" type="checkbox" onChange={this.handleChangePK.bind(this)} checked={this.state.newPortIsPK} name={'Primary Key'}></input>
                                <input className="tray-bottom-item" type="checkbox" onChange={this.handleChangeFK.bind(this)} checked={this.state.newPortIsFK} name={'Foreign Key'}></input>

                                <input className="tray-bottom-item" type="text" onChange={this.handleChange.bind(this)} value={this.state.newPortName}></input>
                                <input className="tray-bottom-item" type="text" onChange={this.handleChangePortType.bind(this)} value={this.state.portState}></input>
                                <button className="tray-bottom-item" onClick={this.switchViewUpdateValue.bind(this)}>Add</button>
                                <button className="tray-bottom-item" onClick={this.cancelAddingPort.bind(this)}>Cancel</button>
                            </div> : <h1 className="tray-bottom-item">{'Add Property'}</h1>}
                    </div>
                    <div onDoubleClick={() => this.showInputUpdate()}>
                        {this.state.showInputUpdate ? this.state.updatedItem.getInPorts().map(p => {
                            return (
                                <div>
                                    <input className="tray-bottom-item" type="checkbox" onChange={this.handleChangePK.bind(this)} checked={p.isPrimaryKey} name={'Primary Key'}></input>
                                    <input className="tray-bottom-item" type="checkbox" onChange={this.handleChangeFK.bind(this)} checked={p.isForeignKey} name={'Foreign Key'}></input>

                                    <input className="tray-bottom-item" type="text" onChange={this.handleChange.bind(this)} value={p.label}></input>
                                    <input className="tray-bottom-item" type="text" onChange={this.handleChangePortType.bind(this)} value={p.propertyType}></input>
                                    <button className="tray-bottom-item" onClick={this.switchViewUpdateValue.bind(this)}>Update</button>
                                    <button className="tray-bottom-item" onClick={this.cancelUpdatingPort.bind(this)}>Cancel</button>
                                </div> 
                            )
                        })
                        : <h1 className="tray-bottom-item">{'Update Property'}</h1>}
                    </div>
                    <div onDoubleClick={() => this.showInputDelete()}>
                        {this.state.showInputDelete ?
                            <div>
                                <select className="tray-bottom-item" onChange={this.selectPort.bind(this)}>{aaa.map(makeItem)}</select>
                                <button className="tray-bottom-item" onClick={() => this.removePort()}>Delete</button>
                                <button className="tray-bottom-item" onClick={this.cancelInputDelete.bind(this)}>Cancel</button>

                            </div> : <h1 className="tray-bottom-item">{'Delete Property'}</h1>}
                    </div>
                    </div>
                    : null }
                    <div onDoubleClick={() => this.showInputColor()}>
                        {this.state.showInputColor ?
                            <div>
                                <select className="tray-bottom-item" onChange={this.selectColor.bind(this)} value={this.state.color}>
                                    <option value="Red">Red</option>
                                    <option value="Pink">Pink</option>
                                    <option value="Green">Green</option>
                                    <option value="Blue">Blue</option>
                                    <option value="Orange">Orange</option>
                                    <option value="Brown">Brown</option>
                                </select>
                                <button className="tray-bottom-item" onClick={() => this.changeColor()}>Change color</button>
                                <button className="tray-bottom-item" onClick={this.cancelChangingColor.bind(this)}>Cancel</button>

                            </div> : <h1 className="tray-bottom-item">{'Change Color'}</h1>}
                    </div>

                </div>


            );

        }
        else return null;

    }
    changeColor(): void {
        if (this.state.color != null) {
            this.state.updatedItem.color = this.state.color;
        }
    }

    selectColor(e: any) {
        e.persist();
        e.preventDefault();

        let colors: string[] = ['Red', 'Pink', 'Green', 'Blue', 'Orange', 'Brown'];
        let colorsRgb: string[] = ['rgb(255,0,0)', 'rgb(255,0,185)', 'rgb(0,250,14)', 'rgb(0, 250, 253)', 'rgb(239, 137, 74)', 'rgb(125, 73, 64)'];
        let index = colors.indexOf(e.target.value);
        let xd = this.state.updatedItem;
        xd.color = colorsRgb[index];
        this.setState({
            color: colorsRgb[index]
        })

    }

    selectPort(e: any) {
        e.persist()
        let ports: { [S: string]: Port } = this.state.updatedItem.ports;

        let names = Object.values(ports).map(value => {
            return value.label
        })
        let portsIds = Object.keys(ports).map((key) => {
            return key
        });

        let indexOfGuyToDelete = names.indexOf(e.target.value);
        let letPortIdToDelete = portsIds[indexOfGuyToDelete];
        console.log('portToRemove', ports, portsIds, indexOfGuyToDelete, ports[indexOfGuyToDelete]);
        this.setState({
            portLabelToRemove: e.target.value,
            portToRemove: ports[letPortIdToDelete]
        });


    }

    showInput(): void {
        this.setState({
            showInput: true
        })
    }

    showInputUpdate(): void {
        this.setState({
            showInputUpdate: true
        })
    }

    showInputDelete(): void {
        this.setState({
            showInputDelete: true
        })
    }

    showInputColor(): void {

        this.setState({
            showInputColor: true,
        })
    }


}
