import React, { useState } from 'react'
import Popup from 'reactjs-popup'
import sqlFormatter from "sql-formatter";
import './popup.scss';
import axios, { AxiosResponse } from 'axios';
import { PopupExample } from './PopupExample';

class Props {
  isOpen: boolean;
  serializeDiagram: any;
}

class State {
    sqlString: string;
}

export class GenerationHandler extends React.Component<Props, State> {
  /**
   *
   */
  constructor(props: Props) {
    super(props);
    this.state = { sqlString: ''}
  }
   generateScript = (name: string) => {
    let x = JSON.stringify(this.props.serializeDiagram, null, 2);
    let res = axios.post('http://localhost:5000/api/setjob', { SerializedDiagram: x, DatabaseName: name })
      .then((response) => {
          this.setState({sqlString: response.data});
          this.forceUpdate();
      })
      .catch(function (error) {
        console.log(error);
      });
   }
    render() {

        return(
            <PopupExample sqlString={this.state.sqlString} generateScript={(name) => this.generateScript(name)} isOpen={this.props.isOpen} />
        )
    }
}
