import * as React from "react";
import { BaseWidget, BaseWidgetProps, PortWidget } from "storm-react-diagrams";
import { Port } from "../models/Port";

export interface PortLabelProps extends BaseWidgetProps {
	model: Port;
}

export interface PortLabelState {}

/**
 * @author Dylan Vorster
 */
export class DefaultPortLabel extends BaseWidget<PortLabelProps, PortLabelState> {
	constructor(props: PortLabelProps) {
		super("with_prop", props);
	}

	getClassName() {
		return super.getClassName() + (this.props.model.in ? this.bem("--in") : this.bem("--out"));
	}

	render() {
        var port = <PortWidget node={this.props.model.getParent()} name={'XD'} />;

		var label = <div className="name">{this.props.model.label}</div>;

		return (
			<div {...this.getProps()}>
				{this.props.model.in ? 'port' : 'label'}
				{this.props.model.in ? 'label' : 'port'}
			</div>
		);
	}
}
