import * as React from "react";
import { AbstractLinkFactory, DiagramEngine } from "storm-react-diagrams";
import { Link } from "../models/Link";
import { DefaultLinkWidget } from "../widgets/LinkWidget";
import { RelationType } from "../../App2";


/**
 * @author Dylan Vorster
 */
export class DefaultLinkFactory extends AbstractLinkFactory<Link> {
	constructor() {
		super("default");
	}

	generateReactWidget(diagramEngine: DiagramEngine, link: Link): JSX.Element {
		return React.createElement(DefaultLinkWidget, {
			link: link,
			diagramEngine: diagramEngine
		});
	}

	getNewInstance(initialConfig?: any): Link {
		return new Link('newLink', RelationType.O2O);
	}

	generateLinkSegment(model: Link, widget: DefaultLinkWidget, selected: boolean, path: string) {
		return (
			<path
				className={selected ? widget.bem("--path-selected") : ""}
				strokeWidth={model.width}
				stroke={model.color}
				d={path}
			/>
		);
	}
}
