import * as _ from "lodash";
import { PortModel, DiagramEngine, LinkModel, DefaultLinkModel, NodeModel, DiagramModel } from "storm-react-diagrams";
import { Link } from "./Link";
import { PropertyType } from "./PropertyType";
import { Node } from "./Node";
import { RelationType } from "../../App2";

export class Port extends PortModel {

	in: boolean;
	label: string;
	propertyType: PropertyType;
	isPrimaryKey: boolean;
	isForeignKey: boolean;
	isNotNull: boolean;
	isUnique: boolean;
	isAutoIncremented: boolean;
	links: { [id: string]: Link };
	iterator: number = 1;
	firstM2M: number = 1;
	selectedRelation: RelationType;
	diagramEngine: DiagramEngine;

	constructor(isPrimaryKey: boolean, isForeignKey: boolean, isInput: boolean, name: string, label: string = null, selectedRelation: RelationType, diagramEngine?: DiagramEngine, propertyType?: PropertyType, id?: string) {
		super(name, "with_prop", id);
		this.in = isInput;
		this.label = label || name;
		this.links = {};
		this.propertyType = propertyType;
		this.isForeignKey = isForeignKey;
		this.isPrimaryKey = isPrimaryKey;
		this.selectedRelation = selectedRelation;
		this.diagramEngine = diagramEngine;

	}

	updatePortRelation(selectedRelation: RelationType) {
		this.selectedRelation = selectedRelation;
	}

	deSerialize(object: any, engine: DiagramEngine) {
		super.deSerialize(object, engine);
		this.in = object.in;
		this.label = object.label;
	}

	serialize() {
		return _.merge(super.serialize(), {
			in: this.in,
			label: this.label,
			propertyType: PropertyType[this.propertyType],
			isForeignKey: this.isForeignKey,
			isPrimaryKey: this.isPrimaryKey,
			isNotNull: this.isNotNull,
			isAutoincremented: this.isAutoIncremented,
			isUnique: this.isUnique
		});
	}

	initialLink(nodeToLink: Node): LinkModel {
		let thisPortNode = this.getNode() as Node;
		let ports = nodeToLink.getInPorts();
		let isThereAlreadyThatPort = ports.filter(p => p.label === `${thisPortNode.name}Id`).length;
		if (isThereAlreadyThatPort === 0) {

			let link = this.createLinkModel();
			if (link !== null) {
				link.setSourcePort(this)
				let newPort = nodeToLink.addInPort(false, true, `${thisPortNode.name}Id`, this.selectedRelation, this.diagramEngine, PropertyType.INT);
				console.log('ports', this, newPort);
				link.setTargetPort(newPort);
				this.diagramEngine.getDiagramModel().addLink(link);
				this.diagramEngine.recalculatePortsVisually();
				console.log('link', link);

				this.selectedRelation = RelationType.none;

			}

		}
		this.selectedRelation = RelationType.none;

		return null;
	}

	link(nodeToLink: Node): LinkModel {

		let thisPortNode = this.getNode() as Node;
		let ports = nodeToLink.getInPorts();
		let isThereAlreadyThatPort = ports.filter(p => p.label === `${thisPortNode.name}Id`).length;
		if (isThereAlreadyThatPort === 0) {
			if (this.iterator === 1) {
				let portLinks = this.getLinks() as { [s: string]: Link }; { }
				let linkToDelete = Object.keys(portLinks).filter(l => portLinks[l].getTargetPort() === ports.filter(p => p.label === 'Id')[0]);

				this.diagramEngine.getDiagramModel().removeLink(linkToDelete[0])
			}


			let link = this.createLinkModel();
			if (link !== null) {
				link.setSourcePort(this)
				let newPort = nodeToLink.addInPort(false, true, `${thisPortNode.name}Id`, this.selectedRelation, this.diagramEngine, PropertyType.INT);
				console.log('ports', this, newPort);
				link.setTargetPort(newPort);
				this.diagramEngine.getDiagramModel().addLink(link);
				this.diagramEngine.recalculatePortsVisually();
				console.log('link', link);

				this.selectedRelation = RelationType.none;

			}

		}
		this.selectedRelation = RelationType.none;

		return null;
	}

	createM2MNode(port1Name: string, port2Name: string) {
		let nodes = this.diagramEngine.getDiagramModel().getNodes() as { [s: string]: Node };
		let isAlredy = Object.keys(nodes).filter(x => nodes[x].name.slice(3) === `${port1Name.slice(3, 8)}Has${port2Name}`);


		if (isAlredy.length === 0) {
			let relationNode = new Node('table', `rl_${port1Name}Has${port2Name}`);
			relationNode.addInPort(true, false, `Id`, this.selectedRelation, this.diagramEngine, PropertyType.INT)
			relationNode.addInPort(false, true, `${port1Name}Id`, this.selectedRelation, this.diagramEngine, PropertyType.INT)
			relationNode.addInPort(false, true, `${port2Name}Id`, this.selectedRelation, this.diagramEngine, PropertyType.INT)
			this.diagramEngine.recalculatePortsVisually();

			return relationNode;
		}

		return null;
	}

	addRelationLink(node1: Node, relationtNode: Node, node2: Node, port: Port, port1Name: string, port2Name: string) {
		let portLinks = this.getLinks() as { [s: string]: Link };

		let link = Object.keys(portLinks).filter(l => portLinks[l].getTargetPort() === port);


		this.diagramEngine.getDiagramModel().removeLink(link[0])


		let link1 = this.createLinkModel();
		let link2 = this.createLinkModel();
		if (!link1 || !link2) {
		} else {
			link1.setSourcePort(node1.getInPorts().filter(p => p.label === 'Id')[0])
			link1.setTargetPort(relationtNode.getInPorts().filter(p => p.label === `${port1Name}Id`)[0])
			link2.setSourcePort(relationtNode.getInPorts().filter(p => p.label === `${port2Name}Id`)[0])
			link2.setTargetPort(node2.getInPorts().filter(p => p.label === 'Id')[0])
			this.diagramEngine.getDiagramModel().addAll(relationtNode, link1, link2);
			this.selectedRelation = RelationType.none;
		}
		this.selectedRelation = RelationType.none;


	}

	canLinkToPort(port: PortModel): boolean {
		if (this.selectedRelation === RelationType.none) {
			return true;
		} else
			if (port instanceof Port) {

				if (this.selectedRelation !== RelationType.M2M && (this.isPrimaryKey && port.isPrimaryKey || this.isPrimaryKey && port.isForeignKey || this.isForeignKey && port.isPrimaryKey)) {
					let nodeToLink = port.getNode() as Node;
					this.link(nodeToLink);
				}
				else if (this.selectedRelation === RelationType.M2M && this.iterator === 1) {
					if (this.firstM2M === 1) {
						let nodeToLink = port.getNode() as Node;
						let relNode = this.createM2MNode((this.getNode() as Node).name, nodeToLink.name);
						if (relNode !== null) {
							this.addRelationLink(this.getNode() as Node, relNode, port.getNode() as Node, port, (this.getNode() as Node).name, nodeToLink.name);
							let app = this.diagramEngine.getDiagramModel().getNodes() as { [s: string]: Node };
							Object.keys(app).forEach(x => app[x].updatePorts(RelationType.none));
						} else {
							let app = this.diagramEngine.getDiagramModel().getNodes() as { [s: string]: Node };
							Object.keys(app).forEach(x => app[x].updatePorts(RelationType.none));

							this.selectedRelation = RelationType.none;
							this.firstM2M = 0;
							this.iterator = 0

						}
					}

					return false;
				}

				return true;
			}

		return false;


	}

	createLinkModel(): LinkModel {
		let link = super.createLinkModel();
		if (this.selectedRelation === RelationType.O2O) {
			return new Link("default", RelationType.O2O);
		} else if (this.selectedRelation === RelationType.O2M) {
			return new Link("default", RelationType.O2M);
		} else if (this.selectedRelation === RelationType.M2M) {
			return new Link("default", RelationType.M2M);
		} else {
			return null;
		}

	}
}
