import * as React from "react";
import { BaseWidget, BaseWidgetProps, PortWidget } from "storm-react-diagrams";
import { Port } from "../models/Port";
import { PropertyType } from "../models/PropertyType";
import { Icon } from 'office-ui-fabric-react/lib/Icon';
import AppContext from "../../context/appContext/AppContext";
import { AppViewType } from "../../App2";
export interface PortLabelProps extends BaseWidgetProps {
  model: Port;
}

export interface DefaultPortLabelState { }

export class PortLabel extends BaseWidget<PortLabelProps, DefaultPortLabelState> {
  static contextType = AppContext;
  constructor(props: PortLabelProps) {
    super("srd-default-port", props);
  }

  getClassName() {
    return super.getClassName() + (this.props.model.in ? this.bem("--in") : this.bem("--in"));
  }

  render() {
    // var port = this.props.model.isForeignKey || this.props.model.isPrimaryKey ? <PortWidget node={this.props.model.getParent()} name={this.props.model.name} /> : null;
    var port: JSX.Element;
    if (!this.props.model.isPrimaryKey) {
      this.props.model.setLocked();
      port = <PortWidget node={this.props.model.getParent()} name={this.props.model.name} />;
    } else {
      port = <PortWidget node={this.props.model.getParent()} name={this.props.model.name} />;
    }

    var label = <div className="name">
      <div>
        {this.props.model.label} {this.props.model.propertyType !== undefined ? this.props.model.propertyType : null}
      </div>
    </div>;
    var isPrimaryKey = this.props.model.isPrimaryKey ? <span className="mi mi-Permissions green" /> : null
    var isForeignKey = this.props.model.isForeignKey ? <span className="mi mi-Permissions green" /> : null
    if (this.context.view === AppViewType.ENTITY && isForeignKey) return <div>{port}</div>;
    else
      return (

            <div {...this.getProps()}>
            <div>
              {isPrimaryKey}
            </div>
            <div>
              {isForeignKey}
            </div>
            <div>
              {label}
            </div>
            <div style={{color: 'red'}}>
              {port}
            </div>

          </div>

      );
  }
}
