import './nodeItems.scss';
import * as React from "react";
import { BaseModel, BaseEntity, BaseModelListener, DefaultNodeModel } from 'storm-react-diagrams';

class OwnProps {
    selectedItem: DefaultNodeModel
}

class State {
    updatedItem: DefaultNodeModel
}

type Props = OwnProps;

export class NodeInfo extends React.Component<Props, State> {

    constructor(props: Props) {
        super(props);
    }

    componentWillMount( ){
        this.setState({
            updatedItem: this.props.selectedItem
        })
    }

    componentWillReceiveProps(nextProps: Props) {

        if(nextProps != this.props) {
            this.setState({
                updatedItem: nextProps.selectedItem
            })
            this.forceUpdate();
        }
    }



    render() {
        if(this.state.updatedItem == null) return 'Loading....' 
        console.log('item', this.props.selectedItem);
        return (
            <div className="trayBottom">
                <h1 onDoubleClick={this.changeName()} className="tray-bottom-item">{this.state.updatedItem.name}</h1>
                <h1 className="tray-bottom-item">{this.state.updatedItem.width}</h1>
                <h1 className="tray-bottom-item">{this.state.updatedItem.height}</h1>
                <h1 className="tray-bottom-item">{this.state.updatedItem.color}</h1>
            </div>
        );
    }
    changeName(): (event: React.MouseEvent<HTMLHeadingElement, MouseEvent>) => void {
        console.log(event);
    }
}
