import * as _ from "lodash";
import { PortModel, DiagramEngine, LinkModel, DefaultLinkModel, NodeModel, DiagramModel } from "storm-react-diagrams";
import { Link } from "./Link";
import { PropertyType } from "./PropertyType";
import { Node } from "./Node";
import { RelationType } from "../../App2";
import { Application } from "../dragAndDrop/Application";

export class Port extends PortModel {
	in: boolean;
    label: string;
	propertyType: PropertyType;
	isPrimaryKey: boolean;
	isForeignKey: boolean;
	links: { [id: string]: Link };
	iterator: number = 1;
	firstM2M: number = 1;
	selectedRelation: RelationType;
	diagramModel: DiagramModel;

	constructor(isPrimaryKey: boolean, isForeignKey: boolean, isInput: boolean, name: string, label: string = null, selectedRelation: RelationType, diagramModel?: DiagramModel ,propertyType?: PropertyType, id?: string) {
		super(name, "with_prop", id);
		this.in = isInput;
        this.label = label || name;
        this.links = {};
		this.propertyType = propertyType;
		this.isForeignKey = isForeignKey;
		this.isPrimaryKey = isPrimaryKey;
		this.selectedRelation = selectedRelation;
		this.diagramModel= diagramModel;
	}

	updatePortRelation(selectedRelation: RelationType) {
		this.selectedRelation = selectedRelation;
	}

	deSerialize(object: any, engine: DiagramEngine) {
		super.deSerialize(object, engine);
		this.in = object.in;
		this.label = object.label;
	}

	serialize() {
		return _.merge(super.serialize(), {
			in: this.in,
            label: this.label,
			propertyType: PropertyType[this.propertyType],
			isForeignKey: this.isForeignKey,
			isPrimaryKey: this.isPrimaryKey
		});
	}

	link(nodeToLink: Node): LinkModel {
//dalczego  przypina do zlego portu?

	let thisPortNode = this.getNode() as Node;
	let ports = nodeToLink.getInPorts();
	let isThereAlreadyThatPort = ports.filter(p => p.label === `${thisPortNode.name}Id`).length;
	if(isThereAlreadyThatPort === 0) {
		if(this.selectedRelation === RelationType.M2M) {
			let link = this.createLinkModel();

			link.setSourcePort(this);
			let ports = nodeToLink.getInPorts();
			link.setTargetPort(ports.filter(p => p.label === 'Id')[0]);
			console.log('port', ports.filter(p => p.label === 'Id')[0].getNode());

			return link;
		} else {
			let link = this.createLinkModel();

			let newPort = nodeToLink.addInPort(false, true, `${thisPortNode.name}Id`, this.selectedRelation, this.diagramModel, PropertyType.INT);
			console.log('newPort', newPort);

			link.setSourcePort(this);

			link.setTargetPort(newPort);
			console.log('NEWLINK', link);
			return link;
		}
	}
	return null;
	}

	canLinkToPort(port: PortModel): boolean {
		if (port instanceof Port) {
			if((this.isPrimaryKey && port.isPrimaryKey || this.isPrimaryKey && port.isForeignKey || this.isForeignKey && port.isPrimaryKey)) {		
				let nodeToLink = port.getNode() as Node;
				this.link(nodeToLink);
			} 
			// else if(this.selectedRelation === RelationType.M2M || this.firstM2M === 1){		
	// 			let nodeToLink = port.getNode() as Node;

	// let thisPortNode = this.getNode() as Node;

	// 			let relationNode = new Node('table', `rl_${thisPortNode.name}Has${nodeToLink.name}`);
	// 			relationNode.addInPort(true, false, `Id`, this.selectedRelation, this.diagramModel, PropertyType.INT)
	// 			relationNode.addInPort(false, true, `${thisPortNode.name}Id`, this.selectedRelation, this.diagramModel, PropertyType.INT)
	// 			relationNode.addInPort(false, true, `${nodeToLink.name}Id`, this.selectedRelation, this.diagramModel, PropertyType.INT)
	// 			this.diagramModel.addNode(relationNode);
	// 			this.link(relationNode);

	// 			this.iterator = 0;
	// 			this.firstM2M = 0;
	// 			return false;

			// }


			// return (this.isPrimaryKey && port.isForeignKey || this.isForeignKey && port.isPrimaryKey) ? true : false
			return true;
		}
		return false;


	}

	createLinkModel(): LinkModel {
		let link = super.createLinkModel();
		if (this.selectedRelation === RelationType.O2O) {
			return new Link("default", RelationType.O2O);
		} else if (this.selectedRelation === RelationType.O2M) {
			return new Link("default", RelationType.O2M);
		} else {
			return new Link("default", RelationType.M2M);
		}

	}
}
