import React, { useState } from 'react'
import Popup from 'reactjs-popup'
import sqlFormatter from "sql-formatter";
import './popup.scss';
import { Link } from './components/models/Link';
import { Label } from './components/models/Label';

class Props {
  isOpen: boolean;
  link: Link;
  update: () => void;
}

export const RelationPopup = (props: Props) => {

  const [left, setLeft] = useState(
    '1, N'
  );

  const [right, setRight] = useState(
    '0, 1'
  );

  const [relationName, setRelationName] = useState(
    'relation'
  );

  // handleChangepRropType(event: React.ChangeEvent<HTMLSelectElement>, row: Port) {
  //   event.persist();
  //   (this.state.updatedItem.getPortFromID(row.id) as Port).propertyType = event.target.value;

  //   this.forceUpdate();
  // }

  let setLeftLabel = (value: string) => {
    setLeft(value);
    let a = props.link.labels[0] as Label;
    a.label = value;
  }

  let setRightLabel = (value: string) => {
    setRight(value);
    let a = props.link.labels[4] as Label;
    a.label = value;
  }

  let setRelationNameLabel = (value: string) => {
    setRelationName(value);
    let a = props.link.labels[2] as Label;
    a.label = value;
  }

  return (


    <Popup
      modal
      closeOnDocumentClick
      open={props.isOpen}
      closeOnEscape
    >
      <div className="SQLResultDialog">
        <select className="darkSelect"
          onChange={(event) => setLeftLabel(event.target.value)}
          value={left}>
          <option value="1, N">1, N</option>
          <option value="0, N">0, N</option>
          <option value="0, 0">0, 0</option>
          <option value="0, 1">0, 1</option>
          <option value="1, 1">1, 1</option>
          <option value="1, 0">1, 0</option>
          <option value="N, M">N, M</option>

        </select>

        <input className="darkInput" type="text" defaultValue={relationName} onChange={(event) => setRelationNameLabel(event.target.value)}></input>


        <select className="darkSelect"
          onChange={(event) => setRightLabel(event.target.value)}
          value={right}>
          <option value="1, N">1, N</option>
          <option value="0, N">0, N</option>
          <option value="0, 0">0, 0</option>
          <option value="0, 1">0, 1</option>
          <option value="1, 1">1, 1</option>
          <option value="1, 0">1, 0</option>
          <option value="N, M">N, M</option>

        </select>
        <button onClick={() => props.update}>SAVE</button>
        {/* <input type="text" onChange={event => setDbName(event.target.value)} value={dbName}/>
      <button onClick={ () => props.generateScript(dbName)}>Generate</button>
        <textarea readOnly={true} value={aa}></textarea>
        <button>Download</button> */}
      </div>
    </Popup>)
}
