import './NodeProperties.scss';
import * as React from "react";
import { DefaultNodeModel, DefaultPortModel, PortModel, DiagramModel, LabelModel, DefaultLinkModel, DiagramEngine } from 'storm-react-diagrams';
import { number } from 'prop-types';
import { randomBytes } from 'crypto';
import { Link } from '../models/Link';
import { Node } from "../models/Node";
import { PropertyType } from '../models/PropertyType';
import { Port } from '../models/Port';
import { RelationType } from '../../App2';
import BootstrapTable from 'react-bootstrap-table-next';
import cellEditFactory from 'react-bootstrap-table2-editor';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
class OwnProps {
    selectedItem: Node;
    diagramEngine: DiagramEngine;
    selectedLink: Link;
    selectedRelation: RelationType;
}

class State {
    updatedItem: Node
    name: string;
}

type Props = OwnProps;

export class NodeProperties extends React.Component<Props, State> {
    constructor(props: Props) {
        super(props);
    }

    componentWillMount() {
        this.setState({
            updatedItem: this.props.selectedItem,
            name: this.props.selectedItem.name,
        })
    }

    componentWillReceiveProps(nextProps: Props) {
        if (nextProps != this.props) {
            this.setState({
                updatedItem: nextProps.selectedItem,
                name: nextProps.selectedItem.name
            })
            this.forceUpdate();
        }
    }

/*
in: boolean;
	label: string;
	propertyType: PropertyType;
	isPrimaryKey: boolean;
	isForeignKey: boolean;
	isNotNull: boolean;
	isUnique: boolean;
	isAutoIncremented: boolean;
	links: { [id: string]: Link };
	iterator: number = 1;
	firstM2M: number = 1;
	selectedRelation: RelationType;
	diagramEngine: DiagramEngine;


*/

handleChangePK(event: React.ChangeEvent<HTMLInputElement>, row: Port) {
    event.persist()
    let updatedPort = row;
    updatedPort.isPrimaryKey = !row.isPrimaryKey;

    (this.state.updatedItem.getPortFromID(row.id) as Port).isPrimaryKey = !row.isPrimaryKey;
}

    render() {
        const columns = [{
            dataField: 'label',
            text: 'Property Name'
        }, {
            dataField: 'propertyType',
            text: 'Property Type'
        }, {
            dataField: 'isPrimaryKey',
            text: 'Is Primary Key',
            formatter: (cellContent: any, row: any) => (
              <div className="checkbox">
                <label>
                  <input type="checkbox" checked={ row.isPrimaryKey } onChange={(event) => this.handleChangePK(event, row)} />
                </label>
              </div>
            )
        }, {
            dataField: 'isForeignKey',
            text: 'Is Foreign Key',
            formatter: (cellContent: any, row: any) => (
              <div className="checkbox">
                <label>
                  <input type="checkbox" checked={ row.isForeignKey } />
                </label>
              </div>
            )
        }, {
            dataField: 'isNotNull',
            text: 'Is Nullable',
            formatter: (cellContent: any, row: any) => (
              <div className="checkbox">
                <label>
                  <input type="checkbox" checked={ row.isNotNull } />
                </label>
              </div>
            )
        }, {
            dataField: 'isAutoIncremented',
            text: 'Is Auto Incremented',
            formatter: (cellContent: any, row: any) => (
              <div className="checkbox">
                <label>
                  <input type="checkbox" checked={ row.isAutoIncremented } />
                </label>
              </div>
            )
        }, {
            dataField: 'isUnique',
            text: 'Is Unique',
            formatter: (cellContent: any, row: any) => (
              <div className="checkbox">
                <label>
                  <input type="checkbox" checked={ row.isUnique } />
                </label>
              </div>
            )
        }];

        let ports = this.state.updatedItem.ports as { [s: string]: Port };
        let portsTable: Port[] = Object.keys(ports).map(x => {
            return ports[x]; 
        });
        return (
            <div className="trayBottom">
                <BootstrapTable
                    keyField="id"
                    data={portsTable}
                    columns={columns}
                    // cellEdit={cellEditFactory({
                    //     mode: 'click',
                    //     blurToSave: true
                    // })}
                    bordered={ false }
                />

            </div>
        )
    }
}
